let timer,links=Array.from(document.getElementsByTagName("a")),fetched=[];function addToHead(e){if(!fetched.find(t=>e===t)){let t=document.createElement("link");t.href=e,t.rel="prefetch",t.as="document",document.head.appendChild(t),fetched.push(e)}}links.forEach(e=>{let t=e.getAttribute("href");e.addEventListener("mouseover",()=>{timer=performance.now(),setTimeout(()=>{timer&&addToHead(t)},250)}),e.addEventListener("mouseout",()=>{timer=null})});
